<!doctype html>
<html lang="{{$lang}}">

<head>
  <meta charset="UTF-8" />
  <meta name="description" content="{{$description}}">
  <meta name="keywords" content="{{$keywords}}">
  <meta name="author" content="HOAI AN">
  <meta property="og:title" content="{{$title}}">
  <meta property="og:description" content="{{$description}}">
  <meta property="og:image" content="/favicon.ico">
  <meta property="og:url" content="{{$app_url}}">
  <meta property="og:type" content="website">
  <meta name="viewport" content="width=device-width, initial-scale=1.0" />
  <link rel="shortcut icon" href="/favicon.ico" type="image/x-icon">
  <link rel="preload" href="/fonts/AlumniSans-Regular.ttf" as="font" crossorigin>
  <link rel="preload" href="/fonts/AlumniSans-Light.ttf" as="font" crossorigin>
  <link rel="preload" href="/fonts/AlumniSans-Medium.ttf" as="font" crossorigin>
  <link rel="preload" href="/fonts//AlumniSans-Bold.ttf" as="font" crossorigin>
  <title>{{$title}}</title>
  <script type="module" crossorigin src="/assets/index-hgooGhCS.js"></script>
  <link rel="stylesheet" crossorigin href="/assets/index-aMUKYroC.css">
</head>

<body>
  <div class="pre-load-container">
    <img height="80px"
      src="data:image/png;base64,"
      alt="app icon">
    <style class="pre-load">
      .pre-load-container {
        --d: 1px;
        position: fixed;
        top: 0px;
        left: 0px;
        width: 100vw;
        height: 100vh;
        display: flex;
        justify-content: center;
        align-items: center;
        background: radial-gradient(circle at var(--d) var(--d),

            #000 calc(var(--d) - 1px),
            #0000 var(--d)) 0 0 / 50px 50px;
        z-index: 100;
      }

      .pre-load-container.hide {
        display: none;
      }
    </style>
  </div>
  <div id="root"></div>
</body>

</html>